// src/layouts/MainLayout.jsx
import React, { useState } from 'react';
import Sidebar from '../components/Sidebar';
import DashboardTable from '../components/DashboardTable';
import { Button } from 'react-bootstrap';

const MainLayout = () => {
  const [sidebarVisible, setSidebarVisible] = useState(true);

  // Shared student data
  const [students, setStudents] = useState([
    { name: 'John Doe', role: 'Admin', email: 'john@example.com' },
    { name: 'Jane Smith', role: 'Editor', email: 'jane@example.com' },
    { name: 'Bob Brown', role: 'Viewer', email: 'bob@example.com' },
    { name: 'Alice Johnson', role: 'Admin', email: 'alice@example.com' },
    { name: 'Tom Clark', role: 'Editor', email: 'tom@example.com' },
    { name: 'Sara Davis', role: 'Viewer', email: 'sara@example.com' },
    { name: 'Mike Wilson', role: 'Admin', email: 'mike@example.com' },
    { name: 'Laura Lee', role: 'Editor', email: 'laura@example.com' },
    { name: 'Steve Rogers', role: 'Viewer', email: 'steve@example.com' },
    { name: 'Emily White', role: 'Admin', email: 'emily@example.com' },
  ]);

  const handleAddStudent = () => {
    const newStudent = {
      name: 'New Student',
      role: 'Viewer',
      email: 'new@student.com',
    };
    setStudents(prev => [...prev, newStudent]);
  };

  const handleRemoveStudent = () => {
    setStudents(prev => prev.slice(0, -1));
  };

  return (
    <div className="d-flex" style={{ height: '100vh' }}>
      {sidebarVisible && (
        <Sidebar
          onAddStudent={handleAddStudent}
          onRemoveStudent={handleRemoveStudent}
        />
      )}

      <div className="flex-grow-1">
        {/* Toggle Button */}
        <div className="p-2 border-bottom d-flex justify-content-between align-items-center">
          <Button
            variant="outline-primary"
            size="sm"
            onClick={() => setSidebarVisible(!sidebarVisible)}
          >
            {sidebarVisible ? 'Hide Sidebar' : 'Show Sidebar'}
          </Button>
        </div>

        {/* Dashboard Table */}
        <DashboardTable data={students} />
      </div>
    </div>
  );
};

export default MainLayout;
